{
  "query": {
    "id": "multi-engine-correlation",
    "type": "correlation",
    "query": "logs:exception WITHIN 10m OF traces:status:error AND metrics:error_rate > 5",
    "start_time": "2025-01-01T00:00:00Z",
    "end_time": "2025-01-01T01:00:00Z",
    "timeout": "60s",
    "correlation_options": {
      "enabled": true,
      "correlation_keys": ["service", "operation"],
      "time_window": "10m",
      "engines": ["logs", "metrics", "traces"]
    }
  }
}
